        $pdf->SetFont('Arial','',14);
        $pdf->Cell(0, 10, utf8_decode('Bs.'.$reserva['tax']), 0, 1, 'J');
        $pdf->SetFont('Arial','B',14);
        $pdf->Cell(50,10,utf8_decode('Total: '),0,0,'J');
        $pdf->SetFont('Arial','',14);
        $pdf->Cell(0, 10, utf8_decode('Bs.'.($reserva['subTotal']+$reserva['tax'])), 0, 1, 'J');
        return $pdf->Output();
    }


    public function generatePdfFromHtml($html)
    {
        // Generate the pdf
        $this->dompdf->getpdf($html);

        // Either stream the pdf to the browser
        //$this->dompdf->stream("doc.pdf");

        // Or get the output to handle it yourself
        return $this->dompdf->output();
    }
